import React from 'react';

class WikiArticleForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {value: '', month: '', year:'', day:'', mostViewedDayOfMonth:''};

    this.handleUrlChange = this.handleUrlChange.bind(this);
    this.handleYearChange = this.handleYearChange.bind(this);
    this.handleMonthChange = this.handleMonthChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleUrlChange(event) {
    this.setState({value: event.target.value});
  }

  handleMonthChange(event) {
    this.setState({month: event.target.value});
  }

  handleYearChange(event) {
    this.setState({year: event.target.value});
  }

  handleSubmit(event) {
    event.preventDefault();
    fetch("/wiki/article/Albert_Einstein/topDay/year/2015/month/10")
      .then(res => res.json())
      .then(
        (result) => {
          alert(result.dayOfMonthTimestamp);
          this.setState({mostViewedDayOfMonth: result.dayOfMonthTimestamp});
        },
        // Note: it's important to handle errors here
        // instead of a catch() block so that we don't swallow
        // exceptions from actual bugs in components.
        (error) => {
          console.log(error);
        }
      )
      .catch((error) => {console.log(error)});
  }

  render() {
    return (
    <div>
      <label>
        Article most viewed date: {this.state.mostViewedDayOfMonth}
      </label>
      <form onSubmit={this.handleSubmit}>
        <label>
          Name:
          <input type="text" value={this.state.value} onChange={this.handleUrlChange} />
        </label>

        <br/>
        <select value={this.state.year} onChange={this.handleYearChange}>
          <option value="2010">2010</option>
          <option value="2011">2011</option>
          <option value="2012">2012</option>
          <option value="2013">2013</option>
          <option value="2014">2014</option>
          <option value="2015">2015</option>
          <option value="2016">2016</option>
          <option value="2017">2017</option>
          <option value="2018">2018</option>
          <option value="2019">2019</option>
          <option value="2020">2020</option>
          <option value="2021">2021</option>
          <option value="2022">2022</option>
        </select>
        <br/>
        <select value={this.state.month} onChange={this.handleMonthChange}>
          <option value="01">Jan</option>
          <option value="02">Feb</option>
          <option value="03">Mar</option>
          <option value="04">Apr</option>
          <option value="05">May</option>
          <option value="06">Jun</option>
          <option value="07">Jul</option>
          <option value="08">Aug</option>
          <option value="09">Sep</option>
          <option value="10">Oct</option>
          <option value="11">Nov</option>
          <option value="12">Dec</option>
        </select>
        <br/>

        <input type="submit" value="Submit" />
      </form>
      </div>
    );
  }
}

export default WikiArticleForm;
